body {
	margin: 2rem;
	color: white;
	background-color: #181823;
	font-size: 1rem;
	line-height: 1.5;
	font-family: arial, sans-serif;
}

button {
	padding: .75rem 1rem;
	border: none;
	font: 1rem / 1.5 monospace;
	border-radius: .75rem;
	cursor: pointer;
	color: #6272A4;
	background-color: #181823;
	transition: background-color .2s;
	&:hover {
		background-color: #292437;
	}
}
